import { trigger, state, style, transition, animate } from '@angular/animations';
import { Component } from '@angular/core';

@Component({
  selector: 'app-our-collections',
  templateUrl: './our-collections.component.html',
  styleUrls: ['./our-collections.component.scss'],
  animations: [
    trigger('carouselAnimation', [
      state('in', style({ opacity: 1 })),
      transition('void => *', [
        style({ opacity: 0 }),
        animate('500ms ease-in')
      ]),
      transition('* => void', [
        animate('500ms ease-out', style({ opacity: 0 }))
      ]),
    ]),
  ],
})
export class OurCollectionsComponent {
  popularFurniture: any = [
    { name: 'Modern Sofa', rating: 4, description: 'Comfortable and stylish sofa for your living room.', price: 799, image: 'data:image/jpeg;base64,' },
    { name: 'Dining Table', rating: 5, description: 'Elegant dining table that seats six.', price: 499, image: 'data:image/jpeg;base64,' },
    { name: 'Office Chair', rating: 4, description: 'Ergonomic office chair with lumbar support.', price: 150, image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQEmCPIbVZy8DCqYI4_LsMUWB-WjnSZC9hhWA&s' },
    { name: 'King Size Bed', rating: 5, description: 'Luxurious king size bed with storage.', price: 1200, image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcStR2HgmaIoqhpGWHJct2Q3qiC5rmu80Klsng&s', objWidthCover: false },
    { name: 'Bookshelf', rating: 4, description: 'Spacious bookshelf for your home or office.', price: 200, image: 'data:image/jpeg;base64,' },
    { name: 'Coffee Table', rating: 3, description: 'Modern coffee table with glass top.', price: 120, image: 'data:image/jpeg;base64,' },
    { name: 'Wardrobe', rating: 5, description: 'Spacious wardrobe with multiple compartments.', price: 600, image: 'https://images.livspace-cdn.com/plain/https://jumanji.livspace-cdn.com/magazine/wp-content/uploads/sites/2/2020/07/27101751/Cover-13-1.png' },
    { name: 'Outdoor Chair', rating: 4, description: 'Comfortable outdoor chair for your patio.', price: 80, image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSeSQ-rwGz9rmD_T-JOT4uHUKBM8cWhzC8KoQ&s', width: '82%', objWidthCover: false },
    { name: 'TV Stand', rating: 5, description: 'Sleek TV stand with storage shelves.', price: 250, image: 'data:image/jpeg;base64,', objWidthCover: false },
    { name: 'Nightstand', rating: 4, description: 'Compact nightstand for your bedroom.', price: 90, image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTJee5aFh3d97xLwLmOY9wLHQkYWtN1clJgBg&s', objWidthCover: false }
  ];

  currentIndex = 0;
  itemsPerPage = 3;
  slideDirection: 'left' | 'right' | 'center' = 'center';

  get displayedFurniture() {
    return this.popularFurniture.slice(this.currentIndex, this.currentIndex + this.itemsPerPage);
  }

  next() {
    if (this.currentIndex + this.itemsPerPage < this.popularFurniture.length) {
      this.currentIndex += this.itemsPerPage;
      this.slideDirection = 'left';
    }
  }

  prev() {
    if (this.currentIndex - this.itemsPerPage >= 0) {
      this.currentIndex -= this.itemsPerPage;
      this.slideDirection = 'right';
    }
  }

  onAnimationDone() {
    this.slideDirection = 'center';
  }

  addToCart(furniture: any) {
    console.log(`${furniture.name} added to cart.`);
  }
}
